        <MudButton Variant=""Variant.Filled"" Color=""Color.Secondary"" OnClick=""@(()=>form.Reset())"" Class=""mx-2"">Reset</MudButton>
        <MudButton Variant=""Variant.Filled"" OnClick=""@(()=>form.ResetValidation())"">Reset Validation</MudButton>
